class Solution:
    # @param A : list of integers
    # @return a list of integers
    def setZeroes(self,A):
        m=len(A)
        n=len(A[0])
        pp=[1]*n
        fp=[0]*n
        ll=[1]*m
        for i in range(m):
            for j in range(n):
                if A[i][j]==0:
                    pp[j]=0
                    ll[i]=0
        a=[]
        for i in ll:
            if i==0:
                a.append(fp)
            else:
                a.append(pp)
        return a


